import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

function Cart() {
    const cart = useSelector((store) => store.cart)
    return ( 
        <div className="">
            <h2 className="text-2xl">Shopping Cart</h2>
            {cart.cartItems.lengh === 0 ? (
                <div>
                    <div>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M15.75 10.5V6a3.75 3.75 0 1 0-7.5 0v4.5m11.356-1.993 1.263 12c.07.665-.45 1.243-1.119 1.243H4.25a1.125 1.125 0 0 1-1.12-1.243l1.264-12A1.125 1.125 0 0 1 5.513 7.5h12.974c.576 0 1.059.435 1.119 1.007ZM8.625 10.5a.375.375 0 1 1-.75 0 .375.375 0 0 1 .75 0Zm7.5 0a.375.375 0 1 1-.75 0 .375.375 0 0 1 .75 0Z" />
                        </svg>

                    </div>
                    <div>
                        <p> Your cart is empty </p>
                        <Link to="/"><button className="px-10 py-5 border border-g">Continue Shopping</button></Link>
                    </div>
                </div>
            ): (
                <div>
                    {cart.cartItems?.map((item)=>{
                        return (
                            <div>
                                <div className="w-full h-20 overflow-hidden">
                                    <img src={item.thumbnail} alt="cart image" className="rounded-lg object-contain shadow-xl mx-auto w-full h-full"/>
                                </div>
                            </div>
                        )
                    })}
                </div>
            )}

        </div>
     );
}

export default Cart;
