import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import authSlice from './Redux/Slices/authSlice';

function Register() {
    const dispatch = useDispatch();
    const auth = useSelector((store) => store.auth)
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
   dispatch(authSlice(formData))
  };

  return (
    <form onSubmit={handleSubmit} className='m-4'>
        <h2 className='m-3'>Register From</h2>
      <div>
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Enter your name here"
          value={formData.name}
          onChange={handleChange}
          required
          className='py-1 px-3 border border-gray-500 rounded-lg mb-3'
        />
      </div>
      <div>
        <input
          type="email"
          id="email"
          name="email"
          placeholder='Enter your email here'
          value={formData.email}
          onChange={handleChange}
          required
          className='py-1 px-3 border border-gray-500 rounded-lg mb-3'
        />
      </div>
      <div>
        <input
          type="password"
          id="password"
          name="password"
          placeholder='Enter your password here'
          value={formData.password}
          onChange={handleChange}
          required
          className='py-1 px-3 border border-gray-500 rounded-lg mb-3'
        />
      </div>

      <div className='grid grid-cols-2'>
        <p className='text-md underline-offset-0 text-blue-600'>Login Here</p>
        <button type="submit" className="rounded-md border border-red-500 text-red-500 px-4 
        py-2 hover:bg-red-500 hover:text-white text-center" onClick={handleSubmit}>Register</button>
      </div>

    </form>
  );
}

export default Register;
