import { useParams } from "react-router-dom";
import { products } from "./Utills/Custom";
function ProductDetail() {
    let params= useParams();
    let productId=params.id; 
    const ProductDetail= products.find(product => product.id == productId);
    return ( 
        <div className="grid grid-cols-2 gap-4 p-6">
            <div className=""> 
                <img src={ProductDetail.image} alt="Product Image"/>
            </div>
            <div className="text-left">
                <h4 className="text-xl text-orange-600">{ProductDetail.name}</h4>
                <p className="text-stone-500 divide-black">Description:</p>
                <p className="text-md">{ProductDetail.description}</p>
                <h5>{ProductDetail.price}</h5>
            </div>
        </div>
     );
}

export default ProductDetail;
