import { useSelector } from "react-redux";

function Cart() {
    const cart = useSelector((store) => store.cart)
    return ( 
        <div className="te">
            <h2>Shopping Cart</h2>
            {cart.cartItems?.map((item)=>{
                return (
                    <div>
                        <div>
                            <img src={item.thumbnail} alt="cart image" />
                        </div>
                    </div>
                )
            })}
        </div>
     );
}

export default Cart;
