import React, { useState } from 'react';

const Filter = ({ options, onSelect }) => {
  const [selectedOption, setSelectedOption] = useState(options[0]);

  const handleSelectChange = (event) => {
    const selectedValue = event.target.value;
    const selectedOption = options.find(option => option.value === selectedValue);
    setSelectedOption(selectedOption);
    onSelect(selectedOption); // Invoke the onSelect callback with the selected option
  };

  return (
    <div className='container flex '>
        <div>
            <select value={selectedOption.value} onChange={handleSelectChange}>
                 <option key={option.value} value={option.value}>Popular</option>
                 <option key={option.value} value={option.value}>High To Low</option>
                 <option key={option.value} value={option.value}>Low To High</option>   

            </select>
        </div>
        <div>

        </div>
    </div>

  );
};

export default Filter;
