import { Link} from "react-router-dom";
import Rating from "react-rating";
import axios from "axios"
import { useEffect, useState } from "react";
function ProductsListing() {
    const [products, setProducts] = useState([]);

    const GetURL ="/api/products/getallproducts"

    useEffect(()=> {
        axios.get(GetURL).then((response) => {
            setProducts(response.data) 
        }).catch(err =>{
            console.log(err);
        })
    },[])
    return ( 
        <div className="container mx-auto">
            <div>

            </div>
            <div className="grid grid-cols-4 gap-4">
                {products.map((items)=>{
                    return(
                        <div key={items._id} className="p-4 h-50">
                           <Link to={`/product/${items.id}`}>
                            <div className="w-full h-56 overflow-hidden">
                                 <img src={items.thumbnail} alt=""className="rounded-lg object-contain shadow-xl mx-auto w-full h-full"/>
                            </div>

                            <h4 className="pt-3 text-sm text-neutral-500 text-left">{items.title}&nbsp;({items.brand})</h4>
                            <p className="text-left"><Rating
                                initialRating={items.rating}
                                className="text-amber-400"
                                emptySymbol="fa fa-star-o fa-1x"
                                fullSymbol="fa fa-star fa-1x"
                                readonly={true}
                                /></p>
                            <p className="text-stone-500 text-md font-medium text-left"><span className="text-orange-600">&#8377;{items.price}</span></p>
                            <button className="hidden cart-hover:block">Add to Cart</button></Link>
                        </div>
                    )
                })}
            </div>
        </div>
     );
}

export default ProductsListing;
