import { Link} from "react-router-dom";
import Rating from "react-rating";
import axios from "axios"
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
function ProductsListing() {
    const [products, setProducts] = useState([]);
    const productItems= useSelector(store => store.productsList.items);

    const GetURL ="/api/products/getallproducts"

    useEffect(()=> {
        axios.get(GetURL).then((response) => {
            setProducts(response.data) 
        }).catch(err =>{
            console.log(err);
        })
    },[])
    return ( 
        <div className="container mx-auto">
            <div>

            </div>
            <div className="grid grid-cols-4 gap-4">
                {products.map((items)=>{
                    return(
                        <Link to={`/product/${items.id}`}> 
                        <div key={items._id} className="p-4 h-50 group text-left">
                            <div className="w-full h-56 overflow-hidden">
                                 <img src={items.thumbnail} alt=""className="rounded-lg object-contain shadow-xl mx-auto w-full h-full"/>
                            </div>

                            <h4 className="pt-3 text-sm text-neutral-500">{items.title}&nbsp;({items.brand})</h4>
                            <p className=""><Rating
                                initialRating={items.rating}
                                className="text-amber-400"
                                emptySymbol="fa fa-star-o fa-1x"
                                fullSymbol="fa fa-star fa-1x"
                                readonly={true}
                                /></p>
                            <p className="text-stone-500 text-md font-medium "><span className="text-orange-600">&#8377;{items.price}</span></p>
                            <button className="hidden group-hover:block">Add to Cart</button>
                        </div></Link>
                    )
                })}
            </div>
        </div>
     );
}

export default ProductsListing;
