import React, { useState } from 'react';

const Filter = ({ options, onSelect }) => {
  const [selectedSortBy, setSelectedSortBy] = useState("popular");
  const [selectedCategory, setSelectedCategory,] = useState("All");

  const handleSelectChange = (event) => {
    const selectedValue = event.target.value;
    const selectedOption = options.find(option => option.value === selectedValue);
    setSelectedOption(selectedOption);
    onSelect(selectedOption); // Invoke the onSelect callback with the selected option
  };

  return (
    <div className='container flex justify-end gap-5 my-5'>
        <div >
            <select value={selectedSortBy} onChange={(e)=>setSelectedSortBy(e.target.value)} className='p-3 border border-gray-500 rounded-lg bg-white'>
                 <option  value="popular">Popular</option>
                 <option  value="htl">High To Low</option>
                 <option  value="lth">Low To High</option>   

            </select>
        </div>
        <div>
            <select value="selectedCategory" onChange={(e)=>setSelectedCategory(e.target.value)} className='p-3 border border-gray-500 rounded-lg bg-white'>
                 <option  value="smartphones">SmartP</option>
                 <option  value="laptops">High To Low</option>
                 <option  value="fragrances">Low To High</option>   
                 <option  value="skincare">Low To High</option>
                 <option  value="groceries">Low To High</option>
                 <option  value="home-decoration">Low To High</option>
            </select>
        </div>
    </div>

  );
};

export default Filter;
