  }

  return (
    <div className='container flex justify-end gap-5 my-5'>
        <div >
            <select value={selectedSortBy} onChange={(e)=>{setSelectedSortBy(e.target.value)}} className='p-3 border border-gray-500 rounded-lg bg-white'>
                 <option  value="popular">Popular</option>
                 <option  value="htl">High To Low</option>
                 <option  value="lth">Low To High</option>   

            </select>
        </div>
        <div>
            <select value={selectedCategory} onChange={(e)=>{
                setSelectedCategory(e.target.value); dispatch(filterProducts(selectedCategory))}} className='p-3 border border-gray-500 rounded-lg bg-white'>
                 <option  value="smartphones">Smart Phones</option>
                 <option  value="laptops">Laptops</option>
                 <option  value="fragrances">Fragrences</option>   
                 <option  value="skincare">Skincare</option>
                 <option  value="groceries">Groceries</option>
                 <option  value="home-decoration">Home Decoration</option>
            </select>
        </div>
    </div>

  );
};

export default Filter;
